import Header from './Header'
import GranimCanvas from "./GranimCanvas"

const layoutStyle = {
  textAlign: center,
  padding: 20,
}



const Layout = props => (
  <div ><style jsx>{`
  div {
    margin-left: auto;
    margin-right: auto;
    text-align: center
  }

  a {
    text-decoration: none;
    color: blue;
    font-family: 'Arial';
  }

  a:hover {
    opacity: 0.6;
  }
`}</style>
    <GranimCanvas/>
    <Header />
    {props.children}
  </div>
)

export default Layout
