import Header from './Header'
import ReactGranimCanvas from "react-granim-canvas"

const layoutStyle = {
  margin: 20,
  padding: 20,
  border: '1px solid #DDD'
}

const Layout = props => (
  <div style={layoutStyle}>
    <ReactGranimCanvas/>
    <Header />
    {props.children}
  </div>
)

export default Layout
