import Header from './Header'
import GranimCanvas from "./GranimCanvas"

const layoutStyle = {
  marginLeft: auto,
  marginRight: auto,
  textAlign: center,
  padding: 20,
}

const Layout = props => (
  <div style={layoutStyle}>
    <GranimCanvas/>
    <Header />
    {props.children}
  </div>
)

export default Layout
